// Importante: Esto se coloca dentro de un nodo "Code" en n8n

function uuidv4() {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function (c) {
    const r = Math.random() * 16 | 0;
    const v = c === 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
}

return [
  {
    json: {
      uuid: uuidv4(),
      now: new Date().toLocaleString('es-MX', {
  timeZone: 'America/Mexico_City'
})
    }
  }
];

const monthOrder = [
    "Enero", "Febrero", "Marzo", "Abril", "Mayo", "Junio",
    "Julio", "Agosto", "Septiembre", "Octubre", "Noviembre", "Diciembre"
];

function sortMonths(months) {
    return months.sort((a, b) => monthOrder.indexOf(a) - monthOrder.indexOf(b));
}

function createEventDaysDictionary(monthEvents) {
    const eventsByDay = monthEvents.reduce((acc, event) => {
        if (event.type === 'individual' && event.content && event.content.day) {
            const day = event.content.day.toString();
            if (!acc[day]) acc[day] = [];
            acc[day].push(event);
        }
        return acc;
    }, {});

    const sortedDays = Object.keys(eventsByDay)
        .map(day => parseInt(day))
        .sort((a, b) => a - b);

    const dictionary = sortedDays.reduce((acc, day, index) => {
        acc[(index + 1).toString()] = day.toString();
        return acc;
    }, {});

    return { dictionary, eventsByDay };
}

function createMonthlyMessage(monthlyGuideData, currentMonth) {
    let message = `*Â¡Eventos de ${currentMonth}!* ðŸ“…\n\n`;

    const monthEvents = monthlyGuideData.filter(item =>
        item.month === currentMonth && item.type === 'individual'
    );

    let eventDaysDictionary = {};
    let eventsByDay = {};
    let hasContent = monthEvents.length === 0 ? "no" : "yes";

    if (monthEvents.length === 0) {
        message += `No hay eventos programados para ${currentMonth}. Â¡Pero cada dÃ­a es una oportunidad para aprender!\n\n`;
    } else {
        const eventData = createEventDaysDictionary(monthEvents);
        eventDaysDictionary = eventData.dictionary;
        eventsByDay = eventData.eventsByDay;

        message += "*Eventos del mes:*\n";
        Object.entries(eventDaysDictionary).forEach(([order, day]) => {
            const dayEvents = eventsByDay[day];
            if (dayEvents && dayEvents.length > 0) {
                message += `${order}. ðŸ—“ DÃ­a ${day}:\n`;
                dayEvents.forEach((event, idx) => {
                    message += `   ${String.fromCharCode(97 + idx)}) "${event.content.title}"\n`;
                });
                message += "\n";
            }
        });
    }

    message += `Â¡Esperamos tu participaciÃ³n! Juntos haremos de este mes una experiencia inolvidable. ðŸŒŸ`;

    return { message, eventDaysDictionary, hasContent };
}

// Obtener la variable MonthlyGuide desde items[0].json
const input = items[0].json;

let monthlyGuideData = [];
let availableMonths = [];

try {
    const parsedMonthlyGuide = JSON.parse(input.MonthlyGuide);
    monthlyGuideData = parsedMonthlyGuide.MonthlyGuide || [];

    availableMonths = [...new Set(monthlyGuideData.map(item => item.month))];
    availableMonths = sortMonths(availableMonths);
} catch (error) {
    throw new Error("Error al procesar MonthlyGuide: " + error.message);
}

const currentMonth = monthOrder[new Date().getMonth()];
const { message, eventDaysDictionary, hasContent } = createMonthlyMessage(monthlyGuideData, currentMonth);

// Salida del nodo
return [
  {
    json: {
      mensaje_guardian_options: message.trim(),
      dias_eventos_enumerados: eventDaysDictionary,
      available_months: availableMonths,
      has_content: hasContent
    }
  }
];

return [
  {
    json: {
      now: new Date().toLocaleString('es-MX', {
  timeZone: 'America/Mexico_City'
})
    }
  }
];

