const htmlResponseBody  = JSON.parse(input.htmlResponseBody);     // string JSON con data_multi
const selectedStudentNo  = parseInt(input.selected_student, 10);

// 2) FunciÃ³n principal
try {
  // Parsear sÃ³lo si viene como string
  const parsed = typeof htmlResponseBody === 'string'
    ? JSON.parse(htmlResponseBody)
    : htmlResponseBody;

  const students = parsed.data_multi;
  if (!Array.isArray(students) || students.length === 0) {
    return [{
      json: {
        status:  "error",
        message: "No se encontraron datos de estudiantes",
