const initialUrl    = `${urlTest}/assistant/community_assistant_chat_whatsapp`;
const statusBaseUrl = `${urlTest}/common_llms/job_status/`;

// 3) Crea el payload
const rawData = {
  user_instruction:        pregunta_guardian,
  role:                    'guardian',
  tenant,
  convo_type:              'customer_service_chat',
  is_limit_reached:        false,
  custom_fields:           { interaction_type: 'FAQ' },
  assistant_instructions:  { context_data: student_id, first_name: nombre_guardian },
  user_id,
  faq:                     true,
};

// 4) Helper de espera
const delay = ms => new Promise(r => setTimeout(r, ms));

try {
  // 5) POST inicial
  console.log('POST ‚Üí', initialUrl, rawData);
  const postResponse = await this.helpers.httpRequest({
    method:  'POST',
    url:     initialUrl,
    body:    rawData,
    json:    true,
    timeout: 90000,
  });

  const jobId = postResponse.job_id;
  if (!jobId) throw new Error('No se recibi√≥ job_id');
  console.log('Job ID:', jobId);

  // 6) Polling con GET
  let finalResult;
  for (let i = 0; i < 12; i++) {
    await delay(5000);
    const statusUrl = `${statusBaseUrl}${jobId}`;
    console.log(`GET ‚Üí ${statusUrl}`);
    const statusResp = await this.helpers.httpRequest({
      method:  'GET',
      url:     statusUrl,
      json:    true,
      timeout: 60000,
    });
    console.log('Status:', statusResp.status);
    if (statusResp.status === 'Finished') {
      finalResult = statusResp.result.model_response;
      break;
    }
    if (statusResp.status === 'failed') {
      throw new Error('El trabajo fall√≥ en el servidor');
    }
    if (i === 11) {
      throw new Error('Timeout esperando finalizaci√≥n');
    }
  }

  // 7) Retorna la respuesta al flujo
  return [{ json: { response: finalResult } }];

} catch (err) {
  console.error('Error en C√≥digo:', err.message);
  return [{ json: { error: err } }];
}

