// 4) Helper: listado de eventos
function createEventList(data, currentMonth) {
  let message = `*¬°Eventos de ${currentMonth}!* üìÖ\n\n`;
  const monthEvents = data.filter(i => i.month === currentMonth);
  const eventDaysDictionary = {};
  let counter = 1;
  const seen = new Set();

  if (!monthEvents.length) {
    message += `No hay eventos programados para ${currentMonth}.\n\n`;
  } else {
    message += "*Eventos del mes:*\n";

    // 4.1 individuales √∫nicos y ordenados
    const indivs = monthEvents
      .filter(e => e.type === "individual" && !seen.has(e.content.title))
      .map(e => ({ day: e.content.day, title: e.content.title }));
    indivs.sort((a, b) => a.day - b.day);
    indivs.forEach(ev => {
      seen.add(ev.title);
      eventDaysDictionary[String(counter)] = String(ev.day);
      message += `${counter}. üóì D√≠a ${ev.day}: "${ev.title}"\n`;
      counter++;
    });

    // 4.2 globals
    monthEvents
      .filter(e => e.type === "global" && e.content.events)
      .forEach(e => {
        Object.keys(e.content.events).forEach(title => {
          if (!seen.has(title)) {
            seen.add(title);
            eventDaysDictionary[String(counter)] = "0";
            message += `${counter}. üåç "${title}"\n`;
            counter++;
          }
        });
      });
  }

  message += `\n*¬øQu√© evento te gustar√≠a conocer m√°s a fondo?* *Escribe* el n√∫mero correspondiente.`;
  return { message, eventDaysDictionary };
}

