  throw new Error('JSON inv√°lido en bodyString: ' + e.message);
}

// 3) Extraer cursos y datos de la semana
const courses = data.courses_with_lessons || [];
const week    = data.week_info           || {};

// 4) Construir el mensaje
let message = `*Informe Semanal* üìÖ\n` +
  `Semana ${week.week_number}/${week.year}\n` +
  `Del ${week.start_date} al ${week.end_date}\n\n`;

courses.forEach((course, i) => {
  message += `*${i+1}. ${course.course_name}*\n`;
  message += `Profesor: ${course.teacher_name}\n`;

  if (Array.isArray(course.lessons) && course.lessons.length) {
    course.lessons.forEach((lesson, j) => {
      const sesiones = lesson.number_of_sessions != null
        ? ` (${lesson.number_of_sessions} sesi√≥n${lesson.number_of_sessions > 1 ? 'es' : ''})`
        : '';
      message += `  ${j+1}) ${lesson.lesson_name} ‚Äî ${lesson.lesson_status}${sesiones}\n`;
    });
  } else {
    message += `_Sin lecciones registradas_\n`;
  }

  message += `\n`;
});

