// 1) Leer variables desde el Set previo
const input = items[0].json;

// 2) Definir orden de meses
const monthOrder = [
  "Enero","Febrero","Marzo","Abril","Mayo","Junio",
  "Julio","Agosto","Septiembre","Octubre","Noviembre","Diciembre",
];

// 3) Funci√≥n para ordenar un array de meses seg√∫n monthOrder
function sortMonths(months) {
  return months.sort(
    (a, b) => monthOrder.indexOf(a) - monthOrder.indexOf(b)
  );
}

// 4) Funci√≥n para crear diccionario d√≠a‚Üíeventos
function createEventDaysDictionary(monthEvents) {
  const eventsByDay = monthEvents.reduce((acc, ev) => {
    if (ev.type === "individual" && ev.content?.day) {
      const d = String(ev.content.day);
      acc[d] = acc[d]||[];
      acc[d].push(ev);
    }
    return acc;
  }, {});
  const sortedDays = Object.keys(eventsByDay)
    .map(d => parseInt(d))
    .sort((a,b)=>a-b);
  const dictionary = sortedDays.reduce((acc, day, i) => {
    acc[String(i+1)] = String(day);
    return acc;
  }, {});
  return { dictionary, eventsByDay };
}

// 5) Funci√≥n para generar el mensaje de WhatsApp
function createMonthlyMessage(data, currentMonth) {
  let msg = `*¬°Eventos de ${currentMonth}!* üìÖ\n\n`;
  const monthEvents = data.filter(
    i => i.month === currentMonth && i.type === "individual"
  );
  const hasContent = monthEvents.length>0 ? "yes":"no";

  if (!monthEvents.length) {
    msg += `No hay eventos programados para ${currentMonth}. ¬°Pero cada d√≠a es una oportunidad para aprender!\n\n`;
  } else {
    const { dictionary, eventsByDay } = createEventDaysDictionary(monthEvents);
    msg += "*Eventos del mes:*\n";
    for (const [order, day] of Object.entries(dictionary)) {
      const evs = eventsByDay[day]||[];
      msg += `${order}. üóì D√≠a ${day}:\n`;
      evs.forEach((ev, idx) => {
        msg += `   ${String.fromCharCode(97+idx)}) "${ev.content.title}"\n`;
      });
      msg += "\n";
    }
  }
  msg += "¬°Esperamos tu participaci√≥n! Juntos haremos de este mes una experiencia inolvidable. üåü";
  return { message: msg, eventDaysDictionary: createEventDaysDictionary(monthEvents).dictionary, hasContent };
}

// 6) Parsear MonthlyGuide (string o ya objeto)
let monthlyGuideData = [];
try {
  const raw = input.MonthlyGuide;
  const parsed = typeof raw === "string" ? JSON.parse(raw) : raw;
  // Si viene envuelto en { MonthlyGuide: [...] }
  monthlyGuideData = Array.isArray(parsed)
    ? parsed
    : parsed.MonthlyGuide || [];
} catch (e) {
  // si falla el parse, dejamos el array vac√≠o
  monthlyGuideData = [];
}

// 7) Sacar los meses disponibles y ordenarlos
let availableMonths = [
  ...new Set(monthlyGuideData.map(i=>i.month))
];
availableMonths = sortMonths(availableMonths);

// 8) Mes actual e invocar creaci√≥n de mensaje
const currentMonth = monthOrder[new Date().getMonth()];
const { message, eventDaysDictionary, hasContent } =
  createMonthlyMessage(monthlyGuideData, currentMonth);

// 9) Devolver la salida que usar√° el resto del workflow
return [{
  json: {
    salida_whatsapp_guardian_function: {
      mensaje_guardian_options: message,
      dias_eventos_enumerados: eventDaysDictionary,
      available_months: availableMonths,
      has_content: hasContent,
    }
  }
}];

return [
  {
    json: {
      now: new Date().toLocaleString('es-MX', {
  timeZone: 'America/Mexico_City'
})
    }
  }
];

