function pad(n){ return n<10? '0'+n : ''+n; }
function fmt(d){
  return `${d.getFullYear()}-${pad(d.getMonth()+1)}-${pad(d.getDate())}`;
}

// 3) Fecha actual
const now = new Date();

let start_date = null;
let end_date   = null;
let response;

// 4) L√≥gica seg√∫n selecci√≥n
if (sel === 1) {
  // Semana pasada lunes‚Üíviernes
  const dow = now.getDay();               // Dom=0, Lun=1‚Ä¶S√°b=6
  // Averiguar lunes de ESTA semana:
  const thisMonday = new Date(now);
  thisMonday.setDate(now.getDate() - ((dow + 6) % 7));
  // Lunes de la SEMANA pasada:
  const lastMonday = new Date(thisMonday);
  lastMonday.setDate(thisMonday.getDate() - 7);
  // Viernes de esa semana:
  const lastFriday = new Date(lastMonday);
  lastFriday.setDate(lastMonday.getDate() + 4);

  start_date = fmt(lastMonday);
  end_date   = fmt(lastFriday);
  response   = { start_date, end_date };
}
else if (sel === 2) {
  // Mes pasado 1¬∞‚Üí√∫ltimo
  // Si estamos en enero (mes 0), retrocedemos al diciembre anterior
  const year  = now.getFullYear();
  const month = now.getMonth();           // junio‚Üí5, mayo‚Üí4
  // Primer d√≠a del mes pasado:
  const firstOfPrevMonth = new Date(year, month - 1, 1);
  // √öltimo d√≠a del mes pasado: d√≠a 0 del mes actual
  const lastOfPrevMonth  = new Date(year, month, 0);

  response = {
    start_date: fmt(firstOfPrevMonth),
    end_date:   fmt(lastOfPrevMonth),
  };
}
else {
  // sel === 3
  response = { selected_number: 3 };
}

// 5) Retornar
return [{
  json: {
    status: 'success',
    ...response
  }
}];

