    const v = c === 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
}

return [
  {
    json: {
      uuid: uuidv4(),
      now: new Date().toLocaleString('es-MX', {
  timeZone: 'America/Mexico_City'
})
    }
  }
];

// Function node
const idInsertado  = $json.user_id_finded;             // viene de la rama de insert
const idActualizado = $json.user_state_edited;      // viene de la rama de update
return [{ json: {
  ...$json,
  user_state_final: idActualizado || idInsertado,
}}];

// Nodo “Function” de n8n
// Valida items[0].json.selection y retorna status con mensajes amistosos

// 1. Leer la selección del nodo Set anterior
const raw = items[0].json.selection;
const sel = Number(raw);

// 2. Preparar la respuesta con mensajes descriptivos
let result;

if (Number.isNaN(sel)) {
  // No es un número
  result = {
    status: 'error',
    message: `Ups, parece que tu elección “${raw}” no es un número. Por favor escribe un número del 1 al 7 para continuar.`
  };
} else if (!Number.isInteger(sel)) {
  // No es entero
  result = {
    status: 'error',
    message: `Veo que has ingresado ${sel}, pero necesitamos un número entero. Por favor elige un número sin decimales entre 1 y 7.`
  };
} else if (sel < 1 || sel > 7) {
  // Fuera de rango
  result = {
    status: 'error',
    message: `Tu selección es ${sel}, pero debe estar entre 1 y 7. Intenta de nuevo con un número dentro de ese rango.`
  };
} else {
  // OK
  result = {
    status: 'success',
    message: `¡Genial! Has seleccionado la opción número ${sel}.`,
    selection: sel
  };
}

// 3. Devolver el resultado para usar en nodos siguientes
return [
  {
    json: result
  }
];

